-- Update store_products to ensure all required fields are populated
UPDATE store_products SET 
  brand = COALESCE(brand, supplier),
  rating_average = COALESCE(rating_average, 4.0 + (RANDOM() * 1.0)),
  rating_count = COALESCE(rating_count, (RANDOM() * 200 + 50)::integer),
  review_count = COALESCE(review_count, rating_count - (RANDOM() * 20)::integer),
  in_stock = COALESCE(in_stock, inventory_quantity > 0),
  featured = COALESCE(featured, false),
  specifications = COALESCE(specifications, '{}'),
  tags = COALESCE(tags, LOWER(REPLACE(name, ' ', ','))),
  material = COALESCE(material, 'Mixed'),
  color = COALESCE(color, 'Standard')
WHERE brand IS NULL OR rating_average IS NULL OR rating_count IS NULL OR review_count IS NULL;